import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

interface YouTubeVideo {
  id: string;
  title: string;
  channelTitle: string;
  duration: string;
  thumbnailUrl: string;
}

Deno.serve(async (req) => {
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const { searchQuery, targetTable, maxResults = 100 } = await req.json();

    if (!searchQuery || !targetTable) {
      return new Response(
        JSON.stringify({ error: 'Missing required parameters: searchQuery and targetTable' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Get YouTube API key from environment
    const youtubeApiKey = Deno.env.get('YOUTUBE_API_KEY');
    if (!youtubeApiKey) {
      return new Response(
        JSON.stringify({ error: 'YouTube API key not configured' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Initialize Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    console.log(`Searching YouTube for: ${searchQuery}`);

    // Search YouTube
    const searchUrl = `https://www.googleapis.com/youtube/v3/search?part=snippet&q=${encodeURIComponent(searchQuery)}&type=video&maxResults=${maxResults}&key=${youtubeApiKey}`;
    const searchResponse = await fetch(searchUrl);
    const searchData = await searchResponse.json();

    if (!searchData.items || searchData.items.length === 0) {
      return new Response(
        JSON.stringify({ error: 'No videos found', imported: 0 }),
        { status: 200, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    console.log(`Found ${searchData.items.length} videos`);

    // Get video details including duration
    const videoIds = searchData.items.map((item: any) => item.id.videoId).join(',');
    const detailsUrl = `https://www.googleapis.com/youtube/v3/videos?part=contentDetails,snippet&id=${videoIds}&key=${youtubeApiKey}`;
    const detailsResponse = await fetch(detailsUrl);
    const detailsData = await detailsResponse.json();

    // Process videos and categorize them
    const videos: any[] = [];
    for (const video of detailsData.items) {
      const videoId = video.id;
      const title = video.snippet.title;
      const channelTitle = video.snippet.channelTitle;
      const duration = formatDuration(video.contentDetails.duration);
      const thumbnailUrl = video.snippet.thumbnails.high?.url || video.snippet.thumbnails.default?.url;
      const videoUrl = `https://www.youtube.com/watch?v=${videoId}`;

      // Categorize based on title and search query
      const category = categorizeVideo(title, searchQuery, targetTable);

      if (targetTable === 'lectures') {
        videos.push({
          category_id: category,
          title: title,
          speaker: channelTitle,
          duration: duration,
          video_url: videoUrl,
          thumbnail_url: thumbnailUrl,
          order_index: videos.length + 1
        });
      } else if (targetTable === 'recitations') {
        videos.push({
          category_id: category,
          title: title,
          reciter: channelTitle,
          duration: duration,
          video_url: videoUrl,
          thumbnail_url: thumbnailUrl,
          order_index: videos.length + 1
        });
      }
    }

    console.log(`Processed ${videos.length} videos, inserting into ${targetTable}`);

    // Clear existing data in the table
    const { error: deleteError } = await supabase
      .from(targetTable)
      .delete()
      .neq('id', '00000000-0000-0000-0000-000000000000'); // Delete all rows

    if (deleteError) {
      console.error('Error clearing table:', deleteError);
    }

    // Insert videos into Supabase
    const { data, error } = await supabase
      .from(targetTable)
      .insert(videos);

    if (error) {
      console.error('Error inserting videos:', error);
      return new Response(
        JSON.stringify({ error: error.message, imported: 0 }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    console.log(`Successfully imported ${videos.length} videos`);

    return new Response(
      JSON.stringify({ 
        success: true, 
        imported: videos.length,
