import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from "npm:@supabase/supabase-js@2";

const SUPABASE_URL = Deno.env.get("SUPABASE_URL")!;
const SERVICE_ROLE = Deno.env.get("SUPABASE_SERVICE_ROLE_KEY")!;
const OPENAI_API_KEY = Deno.env.get("OPENAI_API_KEY");

const supabase = createClient(SUPABASE_URL, SERVICE_ROLE, { auth: { persistSession: false } });

Deno.serve(async (req) => {
  try {
    if (req.method !== "POST") {
      return new Response(JSON.stringify({ error: "Method Not Allowed" }), { status: 405 });
    }

    // Auth
    const auth = req.headers.get("Authorization") || "";
    const { data: user } = await supabase.auth.getUser(auth.replace("Bearer ", ""));
    if (!user?.user) {
      return new Response(JSON.stringify({ error: "Unauthorized" }), { status: 401 });
    }

    const { question, chatHistory } = await req.json();

    if (!question || question.trim().length < 3) {
      return new Response(JSON.stringify({ error: "Question must be at least 3 characters." }), { status: 400 });
    }

    if (!OPENAI_API_KEY) {
      return new Response(JSON.stringify({ error: "OpenAI API key not configured" }), { status: 500 });
    }

    // Build messages array with chat history
    const messages = [
      {
        role: "system",
        content: "You are an Islamic scholar (Sheikh) with deep knowledge of the Quran, Hadith, Islamic jurisprudence (Fiqh), and Islamic history. You provide accurate, respectful, and well-sourced answers to questions about Islam. Always cite Quranic verses (with Surah and Ayah numbers) and authentic Hadiths (with source) when relevant. Be compassionate, patient, and understanding. If you're unsure about something, acknowledge it and suggest consulting a local scholar. Avoid controversial topics and focus on mainstream Islamic teachings. Keep responses concise but comprehensive."
      },
      ...chatHistory,
      { role: "user", content: question }
    ];

    // Call OpenAI API
    const chatRes = await fetch("https://api.openai.com/v1/chat/completions", {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${OPENAI_API_KEY}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        model: "gpt-4o-mini",
        messages,
        temperature: 0.7,
        max_tokens: 1000,
      }),
    });

    if (!chatRes.ok) {
      const errorText = await chatRes.text();
      console.error("OpenAI API error:", errorText);
      return new Response(JSON.stringify({ error: "AI service error", detail: errorText }), { status: 502 });
    }

    const json: any = await chatRes.json();
    const answer = json?.choices?.[0]?.message?.content ?? "";

    if (!answer) {
      return new Response(JSON.stringify({ error: "Empty response from AI" }), { status: 502 });
    }

    // Save chat to database
    const userId = user.user.id;

    // Save user message
    await supabase.from("ai_sheikh_chats").insert({
      user_id: userId,
      message_role: "user",
      message_content: question,
    });

    // Save assistant response
    await supabase.from("ai_sheikh_chats").insert({
      user_id: userId,
      message_role: "assistant",
      message_content: answer,
    });

    return new Response(JSON.stringify({ answer }), {
      status: 200,
      headers: { "Content-Type": "application/json" },
    });
  } catch (e) {
    console.error("Error:", e);
    return new Response(JSON.stringify({ error: "Unhandled error", detail: String(e) }), { status: 500 });
  }
});

