-- Create quran_verses table
create table quran_verses (
  id uuid primary key default gen_random_uuid(),
  arabic text not null,
  translation text not null,
  reference text not null,
  surah_number integer not null,
  verse_number integer not null,
  created_at timestamp with time zone default now()
);

-- Enable RLS
alter table quran_verses enable row level security;

-- Create RLS policies (public read access)
create policy "Anyone can view quran verses" on quran_verses for select using (true);

-- Create index for faster queries
create index idx_quran_verses_surah on quran_verses(surah_number);
create index idx_quran_verses_verse on quran_verses(verse_number);

-- Create hadiths table
create table hadiths (
  id uuid primary key default gen_random_uuid(),
  arabic text not null,
  translation text not null,
  reference text not null,
  collection text not null,
  book_number integer,
  hadith_number integer,
  created_at timestamp with time zone default now()
);

-- Enable RLS
alter table hadiths enable row level security;

-- Create RLS policies (public read access)
create policy "Anyone can view hadiths" on hadiths for select using (true);

-- Create index for faster queries
create index idx_hadiths_collection on hadiths(collection);
create index idx_hadiths_number on hadiths(hadith_number);

-- Create daily_content table to track which verse/hadith is shown each day
create table daily_content (
  id uuid primary key default gen_random_uuid(),
  content_date date not null unique default current_date,
  verse_id uuid references quran_verses(id),
  hadith_id uuid references hadiths(id),
  created_at timestamp with time zone default now()
);

-- Enable RLS
alter table daily_content enable row level security;

-- Create RLS policies (public read access)
create policy "Anyone can view daily content" on daily_content for select using (true);

-- Create index for faster queries
create index idx_daily_content_date on daily_content(content_date);

SELECT 
  (SELECT COUNT(*) FROM quran_verses) as verse_count,
  (SELECT COUNT(*) FROM hadiths) as hadith_count;

