import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

Deno.serve(async (req: Request) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    // Create Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseKey = Deno.env.get('SUPABASE_ANON_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseKey);

    // Get today's date in YYYY-MM-DD format
    const today = new Date();
    const todayStr = today.toISOString().split('T')[0];

    console.log('Fetching daily content for date:', todayStr);

    // Check if we already have daily content for today
    const { data: existingContent, error: fetchError } = await supabase
      .from('daily_content')
      .select(`
        *,
        verse:quran_verses(*),
        hadith:hadiths(*)
      `)
      .eq('content_date', todayStr)
      .single();

    if (fetchError && fetchError.code !== 'PGRST116') {
      console.error('Error fetching existing content:', fetchError);
      throw fetchError;
    }

    // If we have content for today, return it
    if (existingContent && existingContent.verse && existingContent.hadith) {
      console.log('Returning existing daily content');
      return new Response(
        JSON.stringify({
          verse: existingContent.verse,
          hadith: existingContent.hadith,
          date: todayStr,
        }),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        }
      );
    }

    // Otherwise, select new random verse and hadith
    console.log('Selecting new daily content');

    // Get random verse
    const { data: verses, error: verseError } = await supabase
      .from('quran_verses')
      .select('*');

    if (verseError) {
      console.error('Error fetching verses:', verseError);
      throw verseError;
    }

    // Get random hadith
    const { data: hadiths, error: hadithError } = await supabase
      .from('hadiths')
      .select('*');

    if (hadithError) {
      console.error('Error fetching hadiths:', hadithError);
      throw hadithError;
    }

    if (!verses || verses.length === 0 || !hadiths || hadiths.length === 0) {
      throw new Error('No verses or hadiths found in database');
    }

    // Use date-based seeded random selection for consistency
    const dayOfYear = Math.floor(
      (today.getTime() - new Date(today.getFullYear(), 0, 0).getTime()) / 86400000
    );

    const selectedVerse = verses[dayOfYear % verses.length];
    const selectedHadith = hadiths[dayOfYear % hadiths.length];

    console.log('Selected verse:', selectedVerse.id);
    console.log('Selected hadith:', selectedHadith.id);

    // Store the daily content
    const { error: insertError } = await supabase
      .from('daily_content')
      .upsert({
        content_date: todayStr,
        verse_id: selectedVerse.id,
        hadith_id: selectedHadith.id,
      }, {
        onConflict: 'content_date',
      });

    if (insertError) {
      console.error('Error storing daily content:', insertError);
      // Don't throw - we can still return the content
    }

    return new Response(
      JSON.stringify({
        verse: selectedVerse,
        hadith: selectedHadith,
        date: todayStr,
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  } catch (error) {
    console.error('Error in get-daily-content function:', error);
    return new Response(
      JSON.stringify({ error: error.message || 'Internal server error' }),
      {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  }
});
